mapper.insert:

 	SELECT FREE_BOARD_SEQ.NEXTVAL FROM DUAL

 INSERT INTO FREE_BOARD(    BNO 
					       , TITLE 
					       , CONTENT 
					       , WRITER  )
               VALUES(    #{bno}
              		   , #{title}
              		   , #{content}
              		   , #{writer}  )

mapper.update:
UPDATE FREE_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}

UPDATE FREE_BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}
