mapper.insert:

 	SELECT SEARCH_BOARD_SEQ.NEXTVAL FROM DUAL

 INSERT INTO SEARCH_BOARD(    BNO 
					       , ARTIST 
					       , DISTRIBUTOR 
					       , ALBUM  )
               VALUES(    #{bno}
              		   , #{artist}
              		   , #{distributor}
              		   , #{album}  )

mapper.select:
SELECT BNO, ARTIST, DISTRIBUTOR, ALBUM, REGDATE
FROM ( SELECT BNO, ARTIST, DISTRIBUTOR, ALBUM, REGDATE, ROW_NUMBER() OVER(ORDER BY BNO DESC) AS RNUM FROM SEARCH_BOARD WHERE 1=1
) SEARCH WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd} ORDER BY BNO DESC
